sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    return validatePhoneNumber('phone_number');
}

function validatePhoneNumber(field) {
    var phoneNumber = g_form.getValue(field);
    var phoneRegex = /^\+?\d{1,3}[-.\s]?\(?\d{3}\)?[-.\s]?\d{3}[-.\s]?\d{4}$/;
    if (!phoneRegex.test(phoneNumber) && phoneNumber.replace(/\D/g, '').length !== 10) {
        g_form.showErrorBox(field, 'Invalid phone number format. Please enter a valid 10-digit phone number.');
		g_form.clearValue();
		return false;
    }
    return true;
}
// End of script
