sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var start=new Date(g_form.getValue("leave_start_date").toString());
   var end=new Date(g_form.getValue("leave_end_date").toString());
   var today=new Date();

   if(end>today || end==today) {
	if(end>start){
	 var dayDiff=(end-start)/(1000*60*60*24);
     g_form.setValue("total_absence_day",dayDiff);
	}else{
	g_form.clearValue("leave_end_date");
	g_form.showFieldMsg("leave_end_date","End Date cannot be before or equal to start date","error");
	}
   }
   else{
	g_form.clearValue("leave_end_date");
	g_form.showFieldMsg("leave_end_date","You cannot select a past date","error");

   }

}
