sys_script_include.script:
var EnrollmentNumberGenerator = Class.create();
EnrollmentNumberGenerator.prototype = {
    initialize: function() {},

    getNextEnrollmentNumber: function() {
        var prefix = 'ENRC00'; 

        var customTableGr = new GlideRecord('x_1386549_studentm_enrollment_number');
        customTableGr.orderByDesc('sys_created_on');
        customTableGr.query();

        var nextNumber;
        if (customTableGr.next()) {

            var currentNumber = parseInt(customTableGr.getValue('number'), 10);
            nextNumber = currentNumber + 1;

            var newRecord = new GlideRecord('x_1386549_studentm_enrollment_number');
            newRecord.initialize();
            newRecord.setValue('number', nextNumber);
            newRecord.insert();
        }

        var enrollmentNumber = prefix + nextNumber;
        return enrollmentNumber;
    },

    type: 'EnrollmentNumberGenerator'
};
