sysauto_script.script:

var studentGr = new GlideRecord('x_1386549_studentm_student');
studentGr.addQuery('sub_status', 'bgv_completed');
studentGr.addQuery('status', '!=', 'registered');
studentGr.query();

while (studentGr.next()) {
    var completionTime = new GlideDateTime(studentGr.verification_completed_at+"");
    var currentTime = new GlideDateTime();
    completionTime.addDaysLocalTime(2); 


    if (completionTime<currentTime) {
        studentGr.status = 'registered';
        var enrollmentNumber = new EnrollmentNumberGenerator().getNextEnrollmentNumber();
        studentGr.enrollment_number = enrollmentNumber;

        studentGr.update();
    }
}
